CREATE DATABASE moviePortal;


CREATE TABLE movies(
    movie_id SERIAL PRIMARY KEY,
    genre_id INT,
    title_genre VARCHAR(255) NOT NULL,
    title_movie VARCHAR(255) NOT NULL,
    year INT, 
    summary VARCHAR(6500) NOT NULL,
    imageurl TEXT


);

CREATE TABLE genre(
    genre_id INT,
    title_genre VARCHAR(255)
);






    


